#include <stdio.h>
#include <conio.h>
#include <stdlib.h>

int main (){
	
	bool i = true;
	int num = 0;
	int cont = 0;
	int soma = 0;
	float media;
	
	printf("Digite uma sequencia de numeros terminada em 0 e separada por Enter\n");
	
	do{
		scanf("%d",&num);
				
		if( num == 0 ){
			i = false;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
		}else{
			soma += num;
			cont++;
		}
	}while (i);
	
	media = (float)soma / cont;  /*typecast on this line*/
	
	printf(" Media : %f\n\n", media);

	getchar();
}